package com.github.shashi.leetcode;

public class Problem1871 {
    public static void main(String[] args) {
        Problem1871 problem1871 = new Problem1871();
        String s = "";
        boolean ans = problem1871.canReach(s
        ,464,4682);
        System.out.println(ans);
    }
    public boolean canReach(String s, int minJump, int maxJump) {
        return canReachA2(s,0,minJump, maxJump, new Boolean[s.length()]);
    }

    public boolean canReachA2(String s,int i, int minJump, int maxJump, Boolean[] cache) {
        if(i>s.length() || s.charAt(i)!='0')return false;
        if(cache[i] != null)return true;
        if(i==s.length()-1)return true;
        int max = Math.min(s.length()-1,maxJump+i);
        for(int j=i + minJump; j <= max;j++){
            boolean ans = s.charAt(j)=='0' && canReachA2(s,j,minJump,maxJump,cache);
            cache[j] = ans;
            if(cache[j])return true;
        }
        cache[i] = false;
        return cache[i];
    }

    public boolean canReachA1(String s,int i, int minJump, int maxJump) {
        if(i>s.length() || s.charAt(i)!='0')return false;
        if(i==s.length())return true;
        int max = Math.min(s.length()-1,maxJump+i);
        for(int j=i + minJump; j <= max;j++)
            if(s.charAt(j)=='0' && canReachA1(s,j,minJump,maxJump))return true;
        return false;
    }
}